mapper.select:
WITH GroupData AS (SELECT MAX(등록일) as 최신등록일, PartNo, 품명, 대응지, MAX(합계) AS qty FROM TB_PDM_ORDER_ForWeeks a
WHERE a.등록일 BETWEEN #{startDate} and #{endDate}
WHERE a.등록일 BETWEEN DATEADD(month, -1, #{toDay}) and #{toDay}
WHERE a.등록일 BETWEEN DATEADD(MONTH, -5, #{stDate}) AND #{stDate}
WHERE a.등록일 BETWEEN dateadd(day, 1, EOMONTH(#{stDate}, -2)) and eomonth(#{stDate}, -1)
AND ( a.대응지 LIKE N'%위해%' OR a.대응지 LIKE N'%덕주%' OR a.대응지 LIKE N'%비나%' OR a.대응지 LIKE N'%베트남%' ) GROUP BY PartNo, 품명, 대응지), orderSummary as (SELECT DISTINCT a.최신등록일, IIF(a.품명 LIKE N'%3공장', RTRIM(REPLACE(a.품명, N' 3공장', N'')), a.품명) AS carItem, a.PartNo, b.OptionCode2 as doorType, b.OptionCode1 as region, a.대응지 as responder, a.qty FROM GroupData a join TB_QAD_품목option정의마스터찾아보기 b on a.PartNo = b.PartNo), RankedData AS (SELECT *, ROW_NUMBER() over (PARTITION BY PartNo ORDER BY 최신등록일 DESC) AS RN FROM orderSummary), LatestPartNo AS ( SELECT PartNo FROM TB_PDM_ORDER_ForWeeks WHERE 등록일 = (SELECT MAX(등록일) FROM TB_PDM_ORDER_ForWeeks) ) SELECT a.carItem, a.PartNo, a.doorType, a.region, a.responder, IIF(b.PartNo IS NOT NULL, 'c', '') AS confirm, a.qty FROM RankedData a LEFT JOIN LatestPartNo b ON a.PartNo = b.PartNo WHERE a.RN = 1
