mapper.update:
WITH temp_product_list AS ( SELECT * FROM (VALUES
(#{item.itemCode}, #{item.box1}, #{item.box2}, #{item.box3}, #{item.box4}, #{item.box5}, #{item.waitingQuantity}, #{item.packingQuantity})
) AS v (temp_part_no, temp_box1, temp_box2, temp_box3, temp_box4, temp_box5, temp_w_qty, temp_p_qty) ) UPDATE order_mes_box AS m SET box_1 = CASE WHEN m.box_1 IS DISTINCT FROM t.temp_box1 THEN t.temp_box1 ELSE m.box_1 END, box_2 = CASE WHEN m.box_2 IS DISTINCT FROM t.temp_box2 THEN t.temp_box2 ELSE m.box_2 END, box_3 = CASE WHEN m.box_3 IS DISTINCT FROM t.temp_box3 THEN t.temp_box3 ELSE m.box_3 END, box_4 = CASE WHEN m.box_4 IS DISTINCT FROM t.temp_box4 THEN t.temp_box4 ELSE m.box_4 END, box_5 = CASE WHEN m.box_5 IS DISTINCT FROM t.temp_box5 THEN t.temp_box5 ELSE m.box_5 END, w_qty = CASE WHEN m.w_qty IS DISTINCT FROM t.temp_w_qty THEN t.temp_w_qty ELSE m.w_qty END, p_qty = CASE WHEN m.p_qty IS DISTINCT FROM t.temp_p_qty THEN t.temp_p_qty ELSE m.p_qty END, modified_at = now() FROM temp_product_list AS t WHERE m.item_code = t.temp_part_no

update.if:
WITH temp_product_list AS ( SELECT * FROM (VALUES
(#{item.factory}, #{item.itemCode}, #{item.quantity})
) AS v(temp_factory, temp_item_code, temp_qty) ) UPDATE order_mes_stock AS m SET qty = CASE WHEN m.qty IS DISTINCT FROM t.temp_qty THEN t.temp_qty ELSE m.qty END, modified_at = now() FROM temp_product_list AS t WHERE m.factory = t.temp_factory AND m.item_code = t.temp_item_code;

