WITH temp_stock AS ( SELECT * FROM( VALUES
(#{item.date},#{item.itemCode}, #{item.lineStock}, #{item.customerStock})
) AS v (temp_update_date, temp_item_code, temp_line, temp_yra) ) UPDATE order_car AS o SET line = t.temp_line, yra = t.temp_yra, modified_at = now() FROM temp_stock t WHERE o.item_code = t.temp_item_code AND o.upload_date = t.temp_update_date AND o.category = '과부족'

mapper.select:
SELECT car_props, etc
FROM order_ops_plan_raw
WHERE responder = #{responder}
AND st_date = #{stDate}
GROUP BY car_props, etc

SELECT car_props as carProps, '' as etc, SUM(st_month1) as M_Prod, SUM(st_month2) as M1_Prod, SUM(st_month3) as M2_Prod, SUM(st_month4) as M3_Prod, SUM(st_month5) as M4_Prod FROM order_ops_plan_raw WHERE responder = '세원' AND st_date = #{stDate}
AND car_props IN
#{item}
GROUP BY car_props
